import React, { useState } from "react";
import "../styles/Home.css";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const LoginRegisterPage = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [firstname, setFirstname] = useState("");
  const [lastname, setLastname] = useState("");
  const navigate = useNavigate();

  const myMenuFunction = () => {
    var i = document.getElementById("navMenu");

    if (i.className === "nav-menu") {
      i.className += " responsive";
    } else {
      i.className = "nav-menu";
    }
  };

  const login = async () => {
    try {

      var x = document.getElementById("login");
      var y = document.getElementById("register");
      var a = document.getElementById("loginBtn");
      var b = document.getElementById("registerBtn");

      x.style.left = "4px";
      y.style.right = "-520px";
      a.classList.add("white-btn");
      b.classList.remove("white-btn");
      x.style.opacity = 1;
      y.style.opacity = 0;
      // Assuming you want to perform the login action here
      // You can add your axios login request here
      const res = await axios.post(
        "http://localhost:8090/api/v1/employee/login",
        {
          email: email,
          password: password,
        }
      );

      console.log(res.data);
      if (res.data.message === "Email not exists") {
        alert("Email not exists");
      } else if (res.data.message === "Login Success") {
        alert("Login success!");
        navigate("/home");
      } else {
        alert("Incorrect Email and Password do not match");
      }
    } catch (err) {
      console.error(err);
      alert("An error occurred during login");
    }
  };

  const register = async () => {
    try {

      var x = document.getElementById("login");
          var y = document.getElementById("register");
          var a = document.getElementById("loginBtn");
          var b = document.getElementById("registerBtn");
      
          x.style.left = "-510px";
          y.style.right = "5px";
          a.classList.remove("white-btn");
          b.classList.add("white-btn");
          x.style.opacity = 0;
          y.style.opacity = 1;
      // You can add validation or other checks for registration fields here

      // Assuming you want to perform the registration action here
      // You can add your axios registration request here
      const res = await axios.post(
        "http://localhost:8090/api/v1/employee/register",
        {
          firstname: firstname,
          lastname: lastname,
          email: email,
          password: password,
        }
      );

      console.log(res.data);
      if (res.data.message === "Registration Success") {
        alert("Registration success!");
        // Optionally, you can automatically log in the user after registration
        // Navigate to the home page or perform any other desired action
        navigate("/home");
      } else {
        alert("Registration failed. Please check your details.");
      }
    } catch (err) {
      console.error(err);
      alert("An error occurred during registration");
    }
  };

  return (
    <div className="wrapper">
      {/* ... (your existing code) */}
      <div className="register-container" id="register">
        <div className="top">
          <span>
            Have an account?{" "}
            <a href="#" onClick={() => navigate("/login")}>
              Login
            </a>
          </span>
          <header>Sign Up</header>
        </div>
        <div className="two-forms">
          <div className="input-box">
            <input
              type="text"
              className="input-field"
              placeholder="Firstname"
              value={firstname}
              onChange={(event) => setFirstname(event.target.value)}
            />
            <i className="bx bx-user"></i>
          </div>
          <div className="input-box">
            <input
              type="text"
              className="input-field"
              placeholder="Lastname"
              value={lastname}
              onChange={(event) => setLastname(event.target.value)}
            />
            <i className="bx bx-user"></i>
          </div>
        </div>
        <div className="input-box">
          <input
            type="text"
            className="input-field"
            placeholder="Email"
            value={email}
            onChange={(event) => setEmail(event.target.value)}
          />
          <i className="bx bx-envelope"></i>
        </div>
        <div className="input-box">
          <input
            type="password"
            className="input-field"
            placeholder="Password"
            value={password}
            onChange={(event) => setPassword(event.target.value)}
          />
          <i className="bx bx-lock-alt"></i>
        </div>
        <div className="input-box">
          <input
            type="submit"
            className="submit"
            value="Register"
            onClick={register}
          />
        </div>
        <div className="two-col">
          <div className="one">
            <input type="checkbox" id="register-check" />
            <label htmlFor="register-check"> Remember Me</label>
          </div>
          <div className="two">
            <label>
              <a href="#">Terms & conditions</a>
            </label>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginRegisterPage;
